import { useState } from "react";
import { TextField, RadioGroup, FormControlLabel, Radio } from "@mui/material";
import { LocalizationProvider, AdapterDateFns, DatePicker } from "@mui/lab";
import { isUserInputPriceInvalid } from "./util/UserInputUtil.tsx";

import "./CreateTxn.scss";

function CreateTxn() {
    const [amountError, setAmountError] = useState(false);
    const [amount, setAmount] = useState(0);
    const [location, setLocation] = useState("");
    const [date, setDate] = useState(null);

    const amountErrorMessage =
        "Amount entered must be a non-negative, valid price.";

    return (
        <div className="create-main">
            <h1>Create a new transaction here.</h1>
            <form className="create-form">
                <TextField
                    variant="standard"
                    label="Amount"
                    error={amountError}
                    helperText={amountError && amountErrorMessage}
                    onChange={(e) => {
                        if (isUserInputPriceInvalid(e.target.value)) {
                            setAmountError(true);
                        } else {
                            setAmount(Number(e.target.value));
                            setAmountError(false);
                        }
                    }}
                />
                <TextField
                    variant="standard"
                    label="Location"
                    onChange={(e) => setLocation(e.target.value)}
                />
                <RadioGroup row defaultValue="spent">
                    <FormControlLabel
                        value="spent"
                        label="Spent"
                        control={<Radio />}
                    />
                    <FormControlLabel
                        value="received"
                        label="Received"
                        control={<Radio />}
                    />
                </RadioGroup>
                <LocalizationProvider dateAdapter={AdapterDateFns}>
                    <DatePicker label="Date" />
                </LocalizationProvider>
            </form>
        </div>
    );
}

export default CreateTxn;
